import React, { useEffect, useState } from "react";
import back from "../Images/back.png";
import fore_smoke from "../Images/fore-smoke.png";
import fore_tower from "../Images/fore-tower.png";
import mid_rocket from "../Images/mid-rocket.png";
import mid_tower from "../Images/mid-tower.png";

export default function HeroSection() {
  const [offsetY, setOffsetY] = useState(0);
  const [offsetX, setOffsetX] = useState(0);

  const handleMouseMove = (e: any) => {
    setOffsetY(window.screenY);
    setOffsetX(window.screenX);
  };

  useEffect(() => {
    window.addEventListener("mousemove", handleMouseMove);
    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  return (
    <section onMouseMove={handleMouseMove} className="parallax">
      <img
        src={back}
        style={{
          transform: `translateX(${offsetX * -5}px) translateY(${
            offsetY * -5
          }px)`,
        }}
        alt="Background Image"
        className="layer"
      />
      <img
        src={mid_rocket}
        alt="Background Image"
        data-speed="3"
        className="layer"
      />
      <img
        src={mid_tower}
        alt="Background Image"
        data-speed="4"
        className="layer"
      />
      <img
        src={fore_tower}
        alt="Background Image"
        data-speed="-5"
        className="layer"
      />
      <img
        src={fore_smoke}
        alt="Background Image"
        data-speed="7"
        className="layer"
      />
      {/* <h2 className="layer">Where do aliens go for a drink? The space bar.</h2>
       */}
    </section>
  );
}
